      <div style={{ height: '1500px' }}>
        <p>Scroll down to see the button...</p>
        <button 
          onClick={scrollToTop()} 
          style={{ 
            position: 'fixed', 
            bottom: '20px', 
            backgroundColor: 'lightblue', 
            padding: '10px',
